import React,{ PureComponent } from 'react';
import {Link } from 'react-router-dom';
import { Icon ,Col,Row,Button} from 'antd';
import {
    DetailDemo,
    BackTitle
} from './style';
class Detail extends PureComponent{
    render(){
        return (
            <DetailDemo>
                <BackTitle>
                    <Row>
                        <Col span={1} offset={1}>
                            <Link to='/'>
                                <div className='DetailIcon'>
                                    <Icon style={{ fontSize: '18px',color:'white'}} type="arrow-left" />
                                </div>
                            </Link>
                        </Col>
                        <Col  span={1}>
                            <Link to='/'>
                                <div className='DetailText'> 返回</div>
                            </Link>
                        </Col>
                        <Col span={2} offset={16}>
                            <Button >加入购物车</Button>
                        </Col>
                        <Col span={2}>
                            <Button type="primary">立即购买</Button>
                        </Col>
                    </Row>
                </BackTitle>
                <Col span={18} offset={3}>
                    <img className='DetailImg' src='http://img10.360buyimg.com/imgzone/jfs/t21586/53/1075307095/1276467/cd739f7c/5b1faf31N252fb392.jpg' alt='' />
                </Col>

            </DetailDemo>
        )
    }
    componentDidMount(){
        window.scrollTo(0,0)
      }
}

export default Detail;
