import  React,{ PureComponent } from 'react';
import { Modal, Button } from 'antd';
import { connect } from 'react-redux';
import { actions } from '../store';
class Toast extends PureComponent{
    state = {
        ModalText: 'Content of the modal',
        Toastvisible: true,
        ToastconfirmLoading: true,
      }
      showModal = () => {
        this.setState({
            Toastvisible: true,
        });
      }
    
      handleOk = () => {
        this.setState({
          ModalText: 'The modal will be closed after two seconds',
          ToastconfirmLoading: true,
        });
        setTimeout(() => {
          this.setState({
            Toastvisible: false,
            ToastconfirmLoading: false,
          });
        }, 2000);
      }
    
      handleCancel = () => {
        console.log('Clicked cancel button');
        this.setState({
            Toastvisible: false,
        });
      }
    render(){
          const { Toastvisible }=this.props;
          const {ToastconfirmLoading, ModalText } = this.state;
            return (
            <div>
                <Button type="primary" onClick={this.showModal}>
                Open Modal with async logic
                </Button>
                <Modal
                title="Title"
                visible={Toastvisible}
                onOk={this.handleOk}
                confirmLoading={ToastconfirmLoading}
                onCancel={this.handleCancel}
                >
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{Toastvisible}</p>
                </Modal>
            </div>
            );
        }
    }

    const mapState=(state)=>({
      Toastvisible:state.getIn(['Pages','Toastvisible'])
    })
    const mapDispatch=(dispatch)=>({
      showModal(){
        dispatch( actions.showToastAction)
      }
    })
    export default connect(mapState,mapDispatch)(Toast);
