import  React,{ PureComponent } from 'react';
import { Modal } from 'antd';
import { connect } from 'react-redux';
import { actions } from '../store';
class Toast extends PureComponent{
    state = {
        ModalText: 'Content of the modal',
        Toastvisible: true,
        ToastconfirmLoading: true,
      }
      handleOk = () => {
        this.setState({
          ModalText: 'The modal will be closed after two seconds',
          ToastconfirmLoading: true,
        });
        setTimeout(() => {
          this.setState({
            Toastvisible: false,
            ToastconfirmLoading: false,
          });
        }, 2000);
      }
   
    render(){
          const { handleCancel,Toastvisible , ToastTitle}=this.props;
          const {ToastconfirmLoading, ModalText } = this.state;
            return (
            <div>
              <Modal
                title='选择规格'
                visible={Toastvisible}
                onOk={this.handleOk}
                confirmLoading={ToastconfirmLoading}
                onCancel={handleCancel}
                cancelText='取消'
                okText={ToastTitle}
                >
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{ModalText}</p>
                <p>{Toastvisible}</p>
                </Modal>
            </div>
            );
        }
    }

    const mapState=(state)=>({
      Toastvisible:state.getIn(['Pages','Toastvisible']),
      ToastTitle:state.getIn(['Pages','ToastTitle'])
    })
    const mapDispatch=(dispatch)=>({
      handleCancel(){
        console.log(actions.closeToastAction())
        dispatch(actions.closeToastAction())
      }
    })
    export default connect(mapState,mapDispatch)(Toast);
