import  React,{ PureComponent } from 'react';
import { Modal,Radio  } from 'antd';
import { connect } from 'react-redux';
import { actions } from '../store';
const RadioGroup = Radio.Group;
class Toast extends PureComponent{
    render(){
          const {handleOk, handleCancel,Toastvisible , ToastTitle,ToastconfirmLoading}=this.props;
            return (
            <div>
              <Modal
                title='选择规格'
                visible={Toastvisible}
                onOk={handleOk}
                confirmLoading={ToastconfirmLoading}
                onCancel={handleCancel}
                cancelText='取消'
                okText={ToastTitle}
                >
                  <RadioGroup name="radiogroup" defaultValue={1}>
                    <Radio value={1}>天空之境</Radio>
                    <Radio value={2}>亮黑色</Radio>
                    <Radio value={3}>极光色</Radio>
                    <Radio value={5}>赤茶橘</Radio>
                    <Radio value={6}>珠光贝母</Radio>
                  </RadioGroup>
                </Modal>
            </div>
            );
        }
    }

    const mapState=(state)=>({
      Toastvisible:state.getIn(['Pages','Toastvisible']),
      ToastTitle:state.getIn(['Pages','ToastTitle']),
      ToastconfirmLoading:state.getIn(['Pages','ToastconfirmLoading'])
    })
    const mapDispatch=(dispatch)=>({
      handleCancel(){
        console.log(actions.closeToastAction())
        dispatch(actions.closeToastAction())
      },
      handleOk(){
        console.log(666)
      }
    })
    export default connect(mapState,mapDispatch)(Toast);
