import React, {PureComponent } from 'react';
import { connect } from 'react-redux';
import {actions} from '../store';
import { Drawer, Button } from 'antd';


class DrawerDemo extends PureComponent {
  state = { visible: true, childrenDrawer: false };
  
  
  render() {
    const {visible,childrenDrawer} = this.props;
    return (
      <div>
        
        <Drawer
          title="Multi-level drawer"
          width={520}
          closable={false}
          onClose={this.onClose}
          visible={visible}
        >
          <Button type="primary" onClick={this.showChildrenDrawer}>
            Two-level drawer
          </Button>
          <Drawer
            title="Two-level Drawer"
            width={320}
            closable={false}
            onClose={this.onChildrenDrawerClose}
            visible={childrenDrawer}
          >
            This is two-level drawer
          </Drawer>
          <div
            style={{
              position: 'absolute',
              bottom: 0,
              width: '100%',
              borderTop: '1px solid #e8e8e8',
              padding: '10px 16px',
              textAlign: 'right',
              left: 0,
              background: '#fff',
              borderRadius: '0 0 4px 4px',
            }}
          >
            <Button
              style={{
                marginRight: 8,
              }}
              onClick={this.onClose}
            >
              Cancel
            </Button>
            <Button onClick={this.onClose} type="primary">
              Submit
            </Button>
          </div>
        </Drawer>
      </div>
    );
  }

  onClose = () => {
    this.props.closeDrawer()
  };
  showChildrenDrawer = () => {
    this.props.showChildrenDrawerFs()
  };
  onChildrenDrawerClose = () => {
    this.props.closeChildrenDrawerFs()
  };
}

const mapState=(state)=>({
  childrenDrawer:state.getIn(['Header','childrenDrawer']),
  visible:state.getIn(['Header','visible']),
  childrenDrawer:state.getIn(['Header','childrenDrawer'])

})
const mapDispatch=(dispatch)=>({
    closeDrawer(){
      dispatch(actions.closeDrawerAction())
    },
    showChildrenDrawerFs(){
      dispatch(actions.ShowChildrenAction())
    },
    closeChildrenDrawerFs(){
      dispatch(actions.CloseChildrenAction())
    }
})
export default connect(mapState,mapDispatch)(DrawerDemo);
