import React,{ PureComponent } from 'react';
import { Menu, Icon, Switch } from 'antd';
const SubMenu = Menu.SubMenu;
class Header extends PureComponent{
    state = {
        theme: 'dark',
        current: '1',
      }
    
      changeTheme = (value) => {
        this.setState({
          theme: value ? 'dark' : 'light',
        });
      }
    
      handleClick = (e) => {
        console.log('click ', e);
        this.setState({
          current: e.key,
        });
      }
    
    render(){
        return (
            <div>Header</div>
        )
    }
}
export default Header;
