import React,{ PureComponent } from 'react';
import {Link } from 'react-router-dom';
import { PageHeader } from 'antd';
import {
    DetailDemo
} from './style';
class Detail extends PureComponent{
    render(){
        return (
            <DetailDemo>
                <Link>
                <PageHeader
                    onBack={() => null}
                    title="Title"
                    subTitle="This is a subtitle"
                />
                </Link>
            </DetailDemo>
        )
    }
    componentDidMount(){
        window.scrollTo(0,0)
      }
}

export default Detail;
