import React,{ PureComponent } from 'react';
import '../../../node_modules/antd/dist/antd.css';
import { Avatar,Popover } from 'antd';
import {
    Headerbox,
    HeaderItem
} from './style';

class Common extends PureComponent{
    constructor(props){
        super(props);
        this.state={
            text:'Title',
            content:['Content']
        }
    }
    render(){
        return (
            <Headerbox>
                <HeaderItem>
                <Popover placement="rightTop" title={this.state.text} content={this.state.content} trigger="click">
                    <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                </Popover>
                </HeaderItem>
            </Headerbox>
        );
    }
}
export default Common;
